const manufacturer=$("#id_manufacturer"),vehicle=$("#id_vehicle");function updateChoices(){csrf=$('input[name="csrfmiddlewaretoken"]'),$.ajax({type:"GET",url:"/utils/update_choices",datatype:"json",async:!0,data:{csrfmiddlewaretoken:$(csrf).val(),manufacturer:$(manufacturer).val()},success:function(e){$(vehicle).children().remove();for(var c=0;c<e.choices.length;c++)$(vehicle).append(new Option(e.choices[c][1],e.choices[c][0]))}})}manufacturer.on("change",function(){updateChoices()});